import './App.css';
import React, { useState } from 'react';

function App() {
  const [name, setName] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('https://ton-serveur-n8n/webhook/aml-verification', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name })
      });

      const data = await res.json();
      setResponse(`Résultat : ${data.status} - Risque : ${data.risk_score}`);
    } catch (error) {
      console.error('Erreur lors de l’envoi :', error);
      setResponse("Erreur lors de la requête.");
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h2>Vérification AML</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Entrez un nom :</label><br />
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Ex: Ahmed Ben Salah"
          required
        />
        <br /><br />
        <button type="submit">Envoyer</button>
      </form>
      {response && (
        <p style={{ marginTop: '1rem' }}>{response}</p>
      )}
    </div>
  );
}

export default App;
