import React, { useState, useEffect } from "react";
import "./App.css";

export default function AMLChecker() {
  const [name, setName] = useState("");
  const [decision, setDecision] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    setDecision(null);

    try {
      // ‚úÖ MODIFIER ICI : mets bien l'URL r√©elle du webhook n8n
      const res = await fetch(
        `http://localhost:5678/webhook/submit-name?name=${encodeURIComponent(name)}`
      );

      if (!res.ok) throw new Error("Erreur serveur");

      const data = await res.json();

      // ‚úÖ Lecture du r√©sultat
      if (data.result) {
        setDecision(data.result); // "Allowed" ou "Blocked"
      } else {
        setDecision("Ind√©termin√©");
      }
    } catch (err) {
      console.error("Erreur lors de la requ√™te :", err);
      setError("Erreur de connexion au serveur.");
    } finally {
      setIsLoading(false);
    }
  };

  // Charger les statistiques des mod√®les au d√©marrage
  useEffect(() => {
    fetch("http://localhost:5678/model_stats")
      .then((res) => res.json())
      .then((data) => setStats(data))
      .catch((err) => console.warn("Impossible de charger les stats :", err));
  }, []);

  return (
    <div className="App">
      <h1>üîç V√©rificateur AML</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          placeholder="Entrez un nom"
          onChange={(e) => setName(e.target.value)}
          required
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Analyse..." : "V√©rifier"}
        </button>
      </form>

      {/* ‚ùå Message d'erreur */}
      {error && <p className="error">{error}</p>}

      {/* ‚úÖ Affichage du r√©sultat */}
      {decision && !error && (
        <div className="result">
          <h3>R√©sultat :</h3>
          <p
            style={{
              fontSize: "1.5em",
              fontWeight: "bold",
              color: decision === "Blocked" ? "red" : "green",
            }}
          >
            {decision === "Blocked"
              ? "‚õî Bloqu√© ‚Äî Un email a √©t√© envoy√©."
              : "‚úÖ Autoris√©"}
          </p>
        </div>
      )}

      {/* üìä Statistiques */}
      {stats && (
        <div className="stats">
          <h4>üìä Statistiques des mod√®les</h4>
          <ul>
            {Object.entries(stats).map(([modelName, values]) => (
              <li key={modelName}>
                {modelName} ‚Äî Pr√©cision :{" "}
                {Math.round((values.accuracy || values) * 100)}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
