import './App.css';
import React, { useState, useEffect } from "react";

export default function AMLChecker() {
  const [name, setName] = useState("");
  const [decision, setDecision] = useState(null); // <- juste la d√©cision ici
  const [stats, setStats] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name }),
      });

      const data = await res.json();

      if (data && data.final_decision) {
        setDecision(data.final_decision); // <- on stocke uniquement la d√©cision
      } else {
        setDecision("Ind√©termin√©");
      }
    } catch (error) {
      console.error("Erreur lors de la v√©rification AML :", error);
      setDecision("Erreur serveur");
    }
  };

  useEffect(() => {
    fetch("http://localhost:5000/model_stats")
      .then(res => res.json())
      .then(data => setStats(data))
      .catch(err => console.error("Erreur model_stats:", err));
  }, []);

  return (
    <div className="App">
      <h2>üîç V√©rificateur AML</h2>
      <form onSubmit={handleSubmit}>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Entrez un nom"
        />
        <button type="submit">V√©rifier</button>
      </form>

      {decision && (
        <div>
          <h3>R√©sultat :</h3>
          <p style={{ fontSize: "1.5em", fontWeight: "bold" }}>
            {decision === "Blocked" ? "‚õî Bloqu√©" : "‚úÖ Autoris√©"}
          </p>
        </div>
      )}
    </div>
  );
}
