import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    if (!name.trim()) return;

    setLoading(true);
    setResult(null);

    try {
      const response = await axios.post("http://localhost:5000/predict", {
        name,
      });

      setResult(response.data);
    } catch (error) {
      console.error("Erreur lors de la pr√©diction:", error);
      setResult({ error: "Erreur de connexion au serveur" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1>üõ°Ô∏è AML Name Checker</h1>
      <input
        type="text"
        placeholder="Entrez un nom"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <button onClick={handlePredict} disabled={loading}>
        {loading ? "Analyse en cours..." : "V√©rifier"}
      </button>

      {result && (
        <div className="result">
          {result.error ? (
            <p style={{ color: "red" }}>{result.error}</p>
          ) : (
            <>
              <p>üë§ Nom : <strong>{result.name}</strong></p>
              <p>‚úÖ Statut : <strong>{result.result}</strong></p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
