import './App.css';
import React, { useState, useEffect } from "react";

export default function AMLChecker() {
  const [name, setName] = useState("");
  const [result, setResult] = useState(null);
  const [stats, setStats] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name }),
    });
    const data = await res.json();
    setResult(data);
  };

  useEffect(() => {
    fetch("http://localhost:5000/model_stats")
      .then(res => res.json())
      .then(data => setStats(data))
      .catch(err => console.error("Erreur model_stats:", err));
  }, []);

  return (
    <div className="App">
      <h2>üîç V√©rificateur AML</h2>
      <form onSubmit={handleSubmit}>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Entrez un nom"
        />
        <button type="submit">V√©rifier</button>
      </form>

      {result && (
        <div>
          <h3>R√©sultat :</h3>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}

      {stats && (
        <div>
          <h3>üìä Statistiques du Mod√®le :</h3>
          <p>Accuracy : {(stats.accuracy * 100).toFixed(2)}%</p>
          <pre>{JSON.stringify(stats.report, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
