import React, { useState, useEffect } from "react";
import "./App.css";

export default function AMLChecker() {
  const [name, setName] = useState("");
  const [decision, setDecision] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    setDecision(null);

    try {
      const res = await fetch(process.env.REACT_APP_API_URL, {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ name }),
});


      const data = await res.json();

      if (data.final_decision) {
        setDecision(data.final_decision);
      } else {
        setDecision("Ind√©termin√©");
      }
    } catch (err) {
      console.error("Erreur lors de la requ√™te :", err);
      setError("Erreur de connexion au serveur.");
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetch("http://localhost:5678/model_stats")
      .then((res) => res.json())
      .then((data) => setStats(data))
      .catch((err) => console.warn("Impossible de charger les stats :", err));
  }, []);

  return (
    <div className="App">
      <h1>üîç V√©rificateur AML</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          placeholder="Entrez un nom"
          onChange={(e) => setName(e.target.value)}
          required
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Analyse..." : "V√©rifier"}
        </button>
      </form>

      {error && <p className="error">{error}</p>}

      {decision && !error && (
        <div className="result">
          <h3>R√©sultat :</h3>
          <p style={{
            fontSize: "1.5em",
            fontWeight: "bold",
            color: decision === "Blocked" ? "red" : "green"
          }}>
            {decision === "Blocked" ? "‚õî Bloqu√©" : "‚úÖ Autoris√©"}
          </p>
        </div>
      )}

      {stats && (
        <div className="stats">
          <h4>üìä Statistiques des mod√®les</h4>
          <ul>
            {Object.entries(stats).map(([modelName, values]) => (
              <li key={modelName}>
                {modelName} ‚Äî Pr√©cision : {Math.round(values.accuracy * 100)}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
