import './App.css';
import React, { useState } from 'react';

function App() {
  const [name, setName] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim()) {
      setError(" Veuillez entrer un nom.");
      return;
    }

    setError('');
    setResult(null);

    try {
      const response = await fetch("http://localhost:5678/webhook-test/aml-verification", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name }),
      });

      if (!response.ok) {
        throw new Error("Erreur lors de la requÃªte au webhook.");
      }

      const data = await response.json();
      setResult(data);
    } catch (err) {
      setError("Erreur : " + err.message);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h2> VÃ©rification AML</h2>
      <form onSubmit={handleSubmit}>
        <label>Nom Ã  vÃ©rifier :</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ width: '100%', padding: 10, marginTop: 10, marginBottom: 10 }}
        />
        <button type="submit">VÃ©rifier</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {result && (
        <div style={{ marginTop: 20 }}>
          <h3>RÃ©sultat :</h3>
          {result.result === "blocked" ? (
            <>
              <p>ðŸš« Noms similaires trouvÃ©s :</p>
              <ul>
                {result.matches.map((m, i) => (
                  <li key={i}>
                    {m.name} | Score : {m.score}/100 | Risque : {m.risk}
                  </li>
                ))}
              </ul>
            </>
          ) : (
            <>
              <p>âœ… Aucun nom trouvÃ© dans la base. PrÃ©diction par IA :</p>
              {Object.entries(result.decision).map(([model, details], i) => (
                <div key={i} style={{ marginBottom: 10 }}>
                  <strong>{model}</strong><br />
                  PrÃ©diction : {details.prediction}<br />
                  ProbabilitÃ©s :
                  <ul>
                    {Object.entries(details.probabilities).map(([label, p], j) => (
                      <li key={j}>{label} : {(p * 100).toFixed(2)}%</li>
                    ))}
                  </ul>
                </div>
              ))}
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
